<script>
      function tagclick(tag,markerID) {
          eval("var tagValue=tag"+tag+"_"+markerID+".innerHTML");
          tagValue=htmlentities.encode(tagValue);
          if (tagValue.search("1010")==-1) // -1 indicates NOT tagged
          {  //it is NOT tagged so tag it.
             var H = new XMLHttpRequest();
             H.open( "GET", "https://www.hmdb.org/tagwork.asp?task=taguntag&u=&m="+markerID+"&t="+tag+"&v=1",true ); // true for asynchronous request
             H.onload=function(e) { // fires when request completes
                 if (H.readyState==4) {
                      if (H.responseText=="Success") {
                          tagValue=tagValue.replace("1011","1010"); 
                          eval("tag"+tag+"_"+markerID+".innerHTML=htmlentities.decode(tagValue)");
                       }
                      else {eval("tagAlertErrorMessage_"+markerID+".innerHTML=H.responseText.slice(0,14)"); eval("tagAlert_"+markerID+".style.visibility='visible';"); setTimeout(function(){eval("tagAlert_"+markerID+".style.visibility='hidden';")},4000);}
                  }
              }
              H.send( null );
          }  
          else 
          {  //it is tagged, so untag it. 
             var H = new XMLHttpRequest();
             H.open( "GET", "https://www.hmdb.org/tagwork.asp?task=taguntag&u=&m="+markerID+"&t="+tag+"&v=0", true); // true for asynchronous request
             H.onload=function(e) {
                 if (H.readyState==4) {
                      if (H.responseText=="Success") {
                          tagValue=tagValue.replace("1010","1011"); 
                          eval("tag"+tag+"_"+markerID+".innerHTML=htmlentities.decode(tagValue)");
                          } 
                      else {eval("tagAlertErrorMessage_"+markerID+".innerHTML=H.responseText.slice(0,14)"); eval("tagAlert_"+markerID+".style.visibility='visible';"); setTimeout(function(){eval("tagAlert_"+markerID+".style.visibility='hidden';")},4000);}
                  }
              }
              H.send( null );
          };
      };
      
(function(window){
    window.htmlentities = {
        /**
         * Converts a string to its html characters completely.
         *
         * @param {String} str String with unescaped HTML characters
         **/
        encode : function(str) {
            var buf = [];
            
            for (var i=str.length-1;i>=0;i--) {
                buf.unshift(['&#', str[i].charCodeAt(), ';'].join(''));
            }
            
            return buf.join('');
        },
        /**
         * Converts an html characterSet into its original character.
         *
         * @param {String} str htmlSet entities
         **/
        decode : function(str) {
            return str.replace(/&#(\d+);/g, function(match, dec) {
                return String.fromCharCode(dec);
            });
        }
    };
})(window);



 
